import { memo, useEffect, useState } from "react";
import axios from "axios";

import Button from "../../components/Button";
import Input from "../../components/Input"
import styles from './Create.module.css';
import { useParams } from "react-router-dom";
import { useNavigate } from "react-router-dom";

const Create = () => {
    const [currentRobot, setCurrentRobot] = useState(0);
    const [robot, setRobot] = useState({});
    const [name, setName] = useState('');
    const [hasErr, setHasErr] = useState(false)
    const params = useParams()
    const navigate = useNavigate();
    console.log('params', params)
    const {id} = useParams();
    console.log('id', id);
    const robots = [
        '/robot-1.svg',
        '/robot-2.svg',
        '/robot-3.svg',
        '/robot-4.svg',
        '/robot-5.svg',
        '/robot-6.svg',
        '/robot-7.svg',
        '/robot-8.svg',
        '/robot-9.svg',
        '/robot-10.svg'
    ];

    

    if (id) {
        console.log('get')
        const getRobot = async () => {try {
            const robot = await axios.get("/api/robot/"+id, {name, type:currentRobot})    
            //setRobot(robot)
            setCurrentRobot(robot._id)
            setName(robot.name)
            console.log('nnnnname', robot.data)
        } catch {
            setHasErr(true)
        }}
        getRobot();
    }

    const onSubmit = async () => {
        console.log(name, currentRobot)
        try {
            await axios.post("/api/robot/create", {name, type:currentRobot})    
            navigate("/")
        } catch {
            setHasErr(true)
        }
    }

    // const onInputChange = (value) => {
    //     setName(value)
    // }
    
    return (
        <form className={styles.form}>
            <div className={styles.robotContainer}>
                <img className={styles.robot} src={robots[currentRobot]} alt="robot" />
            </div>
            <div className={styles.buttonContainer}>
                {
                    robots.map((url, index) => {
                        return <Button key={url} label={index + 1} onClick={() => setCurrentRobot(index)} />
                    })
                }
            </div>
            
            <Input name={name} hasErr={hasErr} onChange={setName}></Input>
            <Button label="Submit" onClick={onSubmit}></Button>
        </form>
    );
}

export default memo(Create);

